case 'r': newstr.append('\r');
break; /* switch */

case 'n': newstr.append('\n');
break; /* switch */

case 'f': newstr.append('\f');
break; /* switch */

/* PASS a \b THROUGH!! */
case 'b': newstr.append("\\b");
break; /* switch */

case 't': newstr.append('\t');
